#include <stdint.h>
#include <stdio.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

int main(void)
{
	float number = 45.1234567890123456789;
	double number2 = 45.1234567890123456789;
	printf("Number: %f\n", number);
	printf("Number2: %0.14lf\n", number2);
	printf("Number2-scientific: %e\n", number2);

	// float can't match the required precision
	float chargeElectron = -1.60217662e-19;
	printf("chargeElectron as float: %f\n", chargeElectron);
	printf("chargeElectron as scientific: %e\n", chargeElectron);

	// therefore use double (precision)
	float chargeElectron2 = -1.60217662e-19;
	printf("chargeElectron as double float: %0.10lf\n", chargeElectron2);
	printf("chargeElectron as double scientific: %0.10le\n", chargeElectron2);


	return 0;
}
